        fullName: [user.firstName, user.lastName].filter(Boolean).join(' ') || '',
        phone: user.phone || '',
        dateOfBirth: user.dateOfBirth || '',
        email: user.email || '',
        gender: user.gender || '',
        address: user.address || '',
        nationality: user.nationality || '',
        weight: user.weight?.toString() || '',
        height: user.height?.toString() || '',
      });
    }
  }, [user]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.fullName.trim()) {
      newErrors.fullName = 'Full name is required';
    } else if (formData.fullName.trim().length < 2) {
      newErrors.fullName = 'Name must be at least 2 characters';
    }

    if (formData.phone && !/^\+?[\d\s-]{8,15}$/.test(formData.phone)) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (formData.weight) {
      const weight = parseFloat(formData.weight);
      if (isNaN(weight) || weight < 20 || weight > 300) {
        newErrors.weight = 'Weight must be between 20-300 kg';
      }
    }

    if (formData.height) {
      const height = parseFloat(formData.height);
      if (isNaN(height) || height < 50 || height > 250) {
        newErrors.height = 'Height must be between 50-250 cm';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateForm()) {
      Alert.alert('Validation Error', 'Please correct the errors before saving');
      return;
    }

    setLoading(true);
    try {
      const nameParts = formData.fullName.trim().split(' ');
      const firstName = nameParts[0] || user?.firstName || '';
      const lastName = nameParts.slice(1).join(' ') || user?.lastName;

      const updates: Record<string, any> = {};

      if (firstName && firstName !== user?.firstName) {
        updates.firstName = firstName;
      }
      if (lastName !== undefined && lastName !== user?.lastName) {
        updates.lastName = lastName;
      }
      if (formData.phone !== user?.phone) {
        updates.phone = formData.phone || null;
      }
      if (formData.gender !== user?.gender) {
        updates.gender = formData.gender || null;
      }
      if (formData.address !== user?.address) {
        updates.address = formData.address || null;
      }
      if (formData.nationality !== user?.nationality) {
        updates.nationality = formData.nationality || null;
      }
      if (formData.dateOfBirth !== user?.dateOfBirth) {
        updates.dateOfBirth = formData.dateOfBirth || null;
      }
      const currentWeight = user?.weight?.toString() || '';
      if (formData.weight !== currentWeight) {
        if (formData.weight) {
          const weightNum = parseFloat(formData.weight);
          if (!isNaN(weightNum)) {
            updates.weight = weightNum;
          }
        } else {
          updates.weight = null;
        }
      }
      const currentHeight = user?.height?.toString() || '';
      if (formData.height !== currentHeight) {
        if (formData.height) {
          const heightNum = parseFloat(formData.height);
          if (!isNaN(heightNum)) {
            updates.height = heightNum;
          }
        } else {
          updates.height = null;
        }
      }

      if (Object.keys(updates).length === 0) {
        setIsEditing(false);
        return;
      }

      const updatedUser = await authApi.updateProfile(updates);
      updateUser(updatedUser);
      setIsEditing(false);
      Alert.alert('Success', 'Profile updated successfully');
    } catch (err) {
      Alert.alert('Error', err instanceof Error ? err.message : 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (user) {
      setFormData({
        fullName: [user.firstName, user.lastName].filter(Boolean).join(' ') || '',
        phone: user.phone || '',
        dateOfBirth: user.dateOfBirth || '',
        email: user.email || '',
        gender: user.gender || '',
        address: user.address || '',
        nationality: user.nationality || '',
        weight: user.weight?.toString() || '',
        height: user.height?.toString() || '',
      });
    }
    setErrors({});
    setIsEditing(false);
  };

  const handleEditToggle = () => {
    if (isEditing) {
      handleSave();
    } else {
      setIsEditing(true);
    }
  };

  return (
    <ScreenWrapper safeArea backgroundColor={COLORS.white}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => router.back()} style={styles.backButton}>
          <Ionicons name="arrow-back" size={24} color={COLORS.textPrimary} />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Profile Settings</Text>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => router.push('/notifications')}
        >
          <Ionicons name="notifications" size={24} color={COLORS.primary} />
          <View style={styles.notificationBadge}>
            <Text style={styles.notificationBadgeText}>3</Text>
          </View>
        </TouchableOpacity>
      </View>

      <ScrollView 
        showsVerticalScrollIndicator={false}
        contentContainerStyle={styles.scrollContent}
      >
        <View style={styles.mainCard}>
          <View style={styles.sectionHeader}>
            <Ionicons name="person" size={18} color={COLORS.primary} />
            <Text style={styles.sectionTitle}>Personal Details</Text>
            <TouchableOpacity 
              style={styles.editButton}
              onPress={handleEditToggle}
            >
              <Ionicons name={isEditing ? "checkmark" : "pencil"} size={18} color={COLORS.primary} />
            </TouchableOpacity>
          </View>

          <FormInput
            label="Full Name"
            value={formData.fullName}
            onChangeText={(text) => {
              setFormData({...formData, fullName: text});
              if (errors.fullName) setErrors({...errors, fullName: undefined});
            }}
            editable={isEditing}
            placeholder="Enter your full name"
            error={errors.fullName}
          />

          <FormInput
            label="Phone"
            value={formData.phone}
            onChangeText={(text) => {
              setFormData({...formData, phone: text});
              if (errors.phone) setErrors({...errors, phone: undefined});
            }}
            editable={isEditing}
            keyboardType="phone-pad"
            placeholder="e.g., +94 77 123 4567"
            error={errors.phone}
          />

          <DatePicker
            value={formData.dateOfBirth}
            onChange={(date) => setFormData({...formData, dateOfBirth: date})}
            editable={isEditing}
          />

          <FormInput
            label="Email Address"
            value={formData.email}
            onChangeText={(text) => {
              setFormData({...formData, email: text});
              if (errors.email) setErrors({...errors, email: undefined});
            }}
            editable={isEditing}
            keyboardType="email-address"
            placeholder="e.g., john@example.com"
            error={errors.email}
          />

          <GenderDropdown
            value={formData.gender}
            onSelect={(value) => setFormData({...formData, gender: value})}
            editable={isEditing}
          />

          <View style={styles.rowInputs}>
            <View style={styles.halfInput}>
              <FormInput
                label="Weight"
                value={formData.weight}
                onChangeText={(text) => {
                  setFormData({...formData, weight: text});
                  if (errors.weight) setErrors({...errors, weight: undefined});
                }}
                editable={isEditing}
                keyboardType="numeric"
                suffix="kg"
                placeholder="e.g., 70"
                error={errors.weight}
              />
            </View>
            <View style={styles.halfInput}>
              <FormInput
                label="Height"
                value={formData.height}
                onChangeText={(text) => {
                  setFormData({...formData, height: text});
                  if (errors.height) setErrors({...errors, height: undefined});
                }}
                editable={isEditing}
                keyboardType="numeric"
                suffix="cm"
                placeholder="e.g., 175"
                error={errors.height}
              />
            </View>
          </View>

          <FormInput
            label="Address"
            value={formData.address}
            onChangeText={(text) => setFormData({...formData, address: text})}
            editable={isEditing}
            placeholder="Enter your address"
          />

          <FormInput
            label="Nationality"
            value={formData.nationality}
            onChangeText={(text) => setFormData({...formData, nationality: text})}
            editable={isEditing}
            placeholder="e.g., Sri Lankan"
          />
        </View>

        {isEditing ? (
          <View style={styles.buttonContainer}>
            <TouchableOpacity style={styles.saveButton} onPress={handleSave} disabled={loading}>
              <Text style={styles.saveButtonText}>{loading ? 'Saving...' : 'Save Changes'}</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.cancelButton} onPress={handleCancel}>
              <Text style={styles.cancelButtonText}>Cancel</Text>
            </TouchableOpacity>
          </View>
        ) : (
          <TouchableOpacity style={styles.backToProfileButton} onPress={() => router.back()}>
            <Text style={styles.backToProfileText}>Back to Profile</Text>
          </TouchableOpacity>
        )}
      </ScrollView>
    </ScreenWrapper>
  );
}

