  if (label) {
    return (
      <PaperAvatar.Text
        label={label.substring(0, 2).toUpperCase()}
        size={size}
        style={[styles.avatar, style]}
      />
    );
  }

  return (
    <PaperAvatar.Icon
      icon="account"
      size={size}
      style={[styles.avatar, style]}
    />
  );
};

