          </View>
        </View>

        <Text style={styles.sectionTitle}>Payment Method</Text>

        <View style={styles.paymentMethods}>
          <TouchableOpacity 
            style={[styles.methodCard, paymentMethod === 'card' && styles.methodCardActive]}
