import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, TouchableOpacity, ActivityIndicator } from 'react-native';
import { useRouter, useLocalSearchParams } from 'expo-router';
import { Ionicons } from '@expo/vector-icons';
import { ScreenWrapper } from '../../../common/components/layout';
import { COLORS } from '../../../config/theme.config';
import { medicalRecordsStyles as styles } from '../components';
import { reportApi, ReportItem } from '../services/reportApi';

export const SectionDetailScreen: React.FC = () => {
  const router = useRouter();
  const { categoryId, sectionId, sectionName, categoryName } = useLocalSearchParams<{ 
    categoryId: string; 
    sectionId: string; 
    sectionName: string;
    categoryName: string;
  }>();

  const [items, setItems] = useState<ReportItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const id = Number(sectionId);
    if (sectionId && !isNaN(id) && id > 0) {
      loadItems(id);
    } else if (sectionId) {
      setError('Invalid section ID');
      setLoading(false);
    }
  }, [sectionId]);

  const loadItems = async (id: number) => {
    try {
      setLoading(true);
      setError(null);
      const data = await reportApi.getItemsBySection(id);
      setItems(data);
    } catch (err: any) {
      console.error('Error loading items:', err);
      setError(err.message || 'Failed to load items');
    } finally {
      setLoading(false);
    }
  };

  const handleItemPress = (item: ReportItem) => {
    router.push({
      pathname: '/my-reports/[categoryId]/[sectionId]/[itemId]',
      params: { 
        categoryId: categoryId || '',
        sectionId: sectionId || '',
        itemId: item.id.toString(),
        itemName: item.name,
        sectionName: sectionName || '',
        categoryName: categoryName || ''
      }
    } as any);
  };

  return (
    <ScreenWrapper safeArea backgroundColor={COLORS.background}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => router.back()}>
          <Ionicons name="arrow-back" size={24} color={COLORS.primary} />
        </TouchableOpacity>
        <Text style={styles.headerTitle} numberOfLines={1}>{sectionName || 'Section'}</Text>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => router.push('/notifications' as any)}
        >
          <Ionicons name="notifications" size={24} color={COLORS.primary} />
          <View style={styles.notificationBadge}>
            <Text style={styles.badgeText}>3</Text>
          </View>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <ActivityIndicator size="large" color={COLORS.primary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary }}>Loading tests...</Text>
        </View>
      ) : error ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 20 }}>
          <Ionicons name="alert-circle-outline" size={64} color={COLORS.error} />
          <Text style={{ marginTop: 16, color: COLORS.error, textAlign: 'center' }}>{error}</Text>
          <TouchableOpacity 
            style={{ marginTop: 16, paddingHorizontal: 24, paddingVertical: 12, backgroundColor: COLORS.primary, borderRadius: 8 }}
            onPress={() => loadItems(Number(sectionId))}
          >
            <Text style={{ color: '#fff', fontWeight: '600' }}>Retry</Text>
          </TouchableOpacity>
        </View>
      ) : items.length === 0 ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 40 }}>
          <Ionicons name="document-text-outline" size={64} color={COLORS.textSecondary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary, textAlign: 'center' }}>
            No tests available in this section.
          </Text>
        </View>
      ) : (
        <ScrollView 
          showsVerticalScrollIndicator={false}
          contentContainerStyle={styles.scrollContent}
        >
          {items.map((item, index) => (
            <TouchableOpacity
              key={item.id}
              style={[
                styles.investigationCard,
                index === items.length - 1 && styles.lastCard
              ]}
              onPress={() => handleItemPress(item)}
              activeOpacity={0.7}
            >
              <Text style={styles.investigationName}>{item.name}</Text>
              <Ionicons name="chevron-forward" size={20} color={COLORS.textSecondary} />
            </TouchableOpacity>
          ))}
        </ScrollView>
      )}
    </ScreenWrapper>
  );
};

import React, { useState, useCallback } from 'react';
import { View, Text, ScrollView, TouchableOpacity, ActivityIndicator, Modal } from 'react-native';
import { useRouter, useLocalSearchParams, useFocusEffect } from 'expo-router';
import { Ionicons } from '@expo/vector-icons';
import { ScreenWrapper } from '../../../common/components/layout';
import { COLORS } from '../../../config/theme.config';
import { medicalRecordsStyles as styles } from '../components';
import { reportApi, ReportItemValue } from '../services/reportApi';
import { useToast } from '../../../providers/ToastProvider';

export const ItemDetailScreen: React.FC = () => {
  const router = useRouter();
  const { showToast } = useToast();
  const { categoryId, sectionId, itemId, itemName, sectionName } = useLocalSearchParams<{ 
    categoryId: string; 
    sectionId: string; 
    itemId: string;
    itemName: string;
    sectionName: string;
  }>();

  const [values, setValues] = useState<ReportItemValue[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [valueToDelete, setValueToDelete] = useState<ReportItemValue | null>(null);

  const loadValues = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await reportApi.getValuesByItem(Number(itemId));
      setValues(data.sort((a, b) => 
        new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
      ));
    } catch (err: any) {
      console.error('Error loading values:', err);
      setError(err.message || 'Failed to load records');
    } finally {
      setLoading(false);
    }
  };

  useFocusEffect(
    useCallback(() => {
      if (itemId) {
        loadValues();
      }
    }, [itemId])
  );

  const handleAddRecord = () => {
    router.push({
      pathname: '/my-reports/[categoryId]/[sectionId]/[itemId]/add',
      params: { 
        categoryId: categoryId || '',
        sectionId: sectionId || '',
        itemId: itemId || '',
        itemName: itemName || '',
        sectionName: sectionName || ''
      }
    } as any);
  };

  const handleViewRecord = (value: ReportItemValue) => {
    router.push({
      pathname: '/my-reports/[categoryId]/[sectionId]/[itemId]/view/[valueId]',
      params: { 
        categoryId: categoryId || '',
        sectionId: sectionId || '',
        itemId: itemId || '',
        valueId: value.id.toString(),
        itemName: itemName || '',
        sectionName: sectionName || ''
      }
    } as any);
  };

  const confirmDelete = (value: ReportItemValue) => {
    setValueToDelete(value);
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = async () => {
    if (!valueToDelete) return;

    setShowDeleteModal(false);
    try {
      await reportApi.deleteValue(valueToDelete.id);
      setValues(prev => prev.filter(v => v.id !== valueToDelete.id));
      showToast('Record deleted successfully', 'success');
    } catch (err: any) {
      showToast(err.message || 'Failed to delete record', 'error');
    }
    setValueToDelete(null);
  };

  const formatValue = (value: ReportItemValue) => {
    if (value.valueNumber !== null && value.valueNumber !== undefined) {
      return value.valueNumber.toString();
    }
    if (value.valueText) {
      return value.valueText;
    }
    if (value.valueDate) {
      return new Date(value.valueDate).toLocaleDateString('en-GB', {
        timeZone: 'Asia/Colombo',
        day: '2-digit',
        month: 'short',
        year: 'numeric',
      });
    }
    return '-';
  };

  const formatDateSriLanka = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-GB', {
      timeZone: 'Asia/Colombo',
      day: '2-digit',
      month: 'short',
      year: 'numeric',
    });
  };

  const getNotes = (value: ReportItemValue) => {
    if (value.valueText) {
      return value.valueText;
    }
    return '-';
  };

  return (
    <ScreenWrapper safeArea backgroundColor={COLORS.background}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => router.back()}>
          <Ionicons name="arrow-back" size={24} color={COLORS.primary} />
        </TouchableOpacity>
        <Text style={styles.headerTitle} numberOfLines={1}>{itemName || 'Test'} Records</Text>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => router.push('/notifications' as any)}
        >
          <Ionicons name="notifications" size={24} color={COLORS.primary} />
          <View style={styles.notificationBadge}>
            <Text style={styles.badgeText}>3</Text>
          </View>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <ActivityIndicator size="large" color={COLORS.primary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary }}>Loading records...</Text>
        </View>
      ) : error ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 20 }}>
          <Ionicons name="alert-circle-outline" size={64} color={COLORS.error} />
          <Text style={{ marginTop: 16, color: COLORS.error, textAlign: 'center' }}>{error}</Text>
          <TouchableOpacity 
            style={{ marginTop: 16, paddingHorizontal: 24, paddingVertical: 12, backgroundColor: COLORS.primary, borderRadius: 8 }}
            onPress={loadValues}
          >
            <Text style={{ color: '#fff', fontWeight: '600' }}>Retry</Text>
          </TouchableOpacity>
        </View>
      ) : values.length === 0 ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 40 }}>
          <Ionicons name="document-text-outline" size={64} color={COLORS.textSecondary} />
          <Text style={{ marginTop: 16, fontSize: 18, fontWeight: '600', color: COLORS.text }}>
            No records yet
          </Text>
          <Text style={{ marginTop: 8, color: COLORS.textSecondary, textAlign: 'center' }}>
            Tap the button below to add your first record
          </Text>
        </View>
      ) : (
        <ScrollView 
          showsVerticalScrollIndicator={false}
          contentContainerStyle={styles.scrollContentWithButton}
        >
          <View style={styles.tableContainer}>
            <View style={styles.tableHeader}>
              <Text style={[styles.tableHeaderText, styles.dateColumn]}>Date</Text>
              <Text style={[styles.tableHeaderText, styles.valueColumn]}>Value</Text>
              <Text style={[styles.tableHeaderText, styles.notesColumn]}>Notes</Text>
              <Text style={[styles.tableHeaderText, { width: 50, textAlign: 'center' }]}>View</Text>
            </View>

            {values.map((value, index) => (
              <View
                key={value.id}
                style={[
                  styles.tableRow,
                  index % 2 === 0 && styles.tableRowEven,
                  { flexDirection: 'row', alignItems: 'center' }
                ]}
              >
                <Text style={[styles.tableCell, styles.dateColumn, styles.dateText]}>
                  {formatDateSriLanka(value.createdAt)}
                </Text>
                <Text style={[styles.tableCell, styles.valueColumn, styles.valueText]}>
                  {formatValue(value)}
                </Text>
                <Text style={[styles.tableCell, styles.notesColumn, styles.notesText]} numberOfLines={1}>
                  {getNotes(value)}
                </Text>
                <TouchableOpacity
                  style={{ width: 50, alignItems: 'center', justifyContent: 'center', paddingVertical: 8 }}
                  onPress={() => handleViewRecord(value)}
                  activeOpacity={0.7}
                >
                  <Ionicons name="eye-outline" size={20} color={COLORS.primary} />
                </TouchableOpacity>
              </View>
            ))}
          </View>
        </ScrollView>
      )}

      <View style={styles.buttonContainer}>
        <TouchableOpacity
          style={styles.addButton}
          onPress={handleAddRecord}
          activeOpacity={0.8}
        >
          <Ionicons name="add" size={22} color="#fff" />
          <Text style={styles.addButtonText}>Add New {itemName || ''} Record</Text>
        </TouchableOpacity>
      </View>

      <Modal
        visible={showDeleteModal}
        transparent
        animationType="fade"
        onRequestClose={() => setShowDeleteModal(false)}
      >
        <View style={{
          flex: 1,
          backgroundColor: 'rgba(0,0,0,0.5)',
          justifyContent: 'center',
          alignItems: 'center',
          padding: 20,
        }}>
          <View style={{
            backgroundColor: '#fff',
            borderRadius: 16,
            padding: 24,
            width: '100%',
            maxWidth: 340,
          }}>
            <View style={{ alignItems: 'center', marginBottom: 16 }}>
              <View style={{
                width: 56,
                height: 56,
                borderRadius: 28,
                backgroundColor: '#ffebee',
                justifyContent: 'center',
                alignItems: 'center',
                marginBottom: 12,
              }}>
                <Ionicons name="trash-outline" size={28} color={COLORS.error} />
              </View>
              <Text style={{ fontSize: 18, fontWeight: '700', color: COLORS.text, textAlign: 'center' }}>
                Delete Record?
              </Text>
            </View>
            <Text style={{ fontSize: 14, color: COLORS.textSecondary, textAlign: 'center', marginBottom: 24, lineHeight: 20 }}>
              Are you sure you want to delete this record? This action cannot be undone.
            </Text>
            <View style={{ flexDirection: 'row', gap: 12 }}>
              <TouchableOpacity
                style={{
                  flex: 1,
                  paddingVertical: 14,
                  borderRadius: 10,
                  backgroundColor: '#f5f5f5',
                  alignItems: 'center',
                }}
                onPress={() => {
                  setShowDeleteModal(false);
                  setValueToDelete(null);
                }}
              >
                <Text style={{ fontSize: 16, fontWeight: '600', color: COLORS.text }}>Cancel</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  flex: 1,
                  paddingVertical: 14,
                  borderRadius: 10,
                  backgroundColor: COLORS.error,
                  alignItems: 'center',
                }}
                onPress={handleDeleteConfirm}
              >
                <Text style={{ fontSize: 16, fontWeight: '600', color: '#fff' }}>Delete</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </ScreenWrapper>
  );
};

import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, TouchableOpacity, ActivityIndicator } from 'react-native';
import { useRouter, useLocalSearchParams } from 'expo-router';
import { Ionicons, MaterialCommunityIcons } from '@expo/vector-icons';
import { ScreenWrapper } from '../../../common/components/layout';
import { COLORS } from '../../../config/theme.config';
import { medicalRecordsStyles as styles } from '../components';
import { reportApi, ReportSection } from '../services/reportApi';

const SECTION_THEMES = [
  { bgColor: '#E3F2FD', iconBg: '#BBDEFB', iconColor: '#1976D2' },
  { bgColor: '#E8F5E9', iconBg: '#C8E6C9', iconColor: '#388E3C' },
  { bgColor: '#F3E5F5', iconBg: '#E1BEE7', iconColor: '#7B1FA2' },
  { bgColor: '#FFF3E0', iconBg: '#FFE0B2', iconColor: '#F57C00' },
  { bgColor: '#FFEBEE', iconBg: '#FFCDD2', iconColor: '#D32F2F' },
  { bgColor: '#E0F7FA', iconBg: '#B2EBF2', iconColor: '#0097A7' },
];

const SECTION_ICONS: Record<string, { icon: string; iconType: 'ionicons' | 'material-community' }> = {
  'core informations': { icon: 'information-circle', iconType: 'ionicons' },
  'core investigations': { icon: 'flask', iconType: 'ionicons' },
  'treatment': { icon: 'medkit', iconType: 'ionicons' },
  'medications': { icon: 'medical', iconType: 'ionicons' },
  'self-management': { icon: 'heart', iconType: 'ionicons' },
  'lifestyle': { icon: 'heart', iconType: 'ionicons' },
  'urgent': { icon: 'warning', iconType: 'ionicons' },
  'red-flag': { icon: 'alert-circle', iconType: 'ionicons' },
  'cardiac': { icon: 'heart-pulse', iconType: 'material-community' },
  'blood pressure': { icon: 'pulse', iconType: 'ionicons' },
  'lipid': { icon: 'water', iconType: 'ionicons' },
  'imaging': { icon: 'scan', iconType: 'ionicons' },
  'risk': { icon: 'shield-checkmark', iconType: 'ionicons' },
  'prenatal': { icon: 'fitness', iconType: 'ionicons' },
  'ultrasound': { icon: 'radio', iconType: 'ionicons' },
  'blood work': { icon: 'water', iconType: 'ionicons' },
  'glucose': { icon: 'analytics', iconType: 'ionicons' },
  'complete blood': { icon: 'water', iconType: 'ionicons' },
  'metabolic': { icon: 'flask', iconType: 'ionicons' },
  'thyroid': { icon: 'pulse', iconType: 'ionicons' },
  'liver': { icon: 'fitness', iconType: 'ionicons' },
  'kidney': { icon: 'filter', iconType: 'ionicons' },
  'x-ray': { icon: 'scan', iconType: 'ionicons' },
  'ct scan': { icon: 'layers', iconType: 'ionicons' },
  'mri': { icon: 'scan-circle', iconType: 'ionicons' },
  'skin': { icon: 'hand-left', iconType: 'ionicons' },
  'allergy': { icon: 'alert', iconType: 'ionicons' },
  'eeg': { icon: 'pulse', iconType: 'ionicons' },
  'nerve': { icon: 'flash', iconType: 'ionicons' },
  'brain': { icon: 'ellipse', iconType: 'ionicons' },
};

const getSectionIcon = (sectionName: string) => {
  const nameLower = sectionName.toLowerCase();
  for (const [key, value] of Object.entries(SECTION_ICONS)) {
    if (nameLower.includes(key)) {
      return value;
    }
  }
  return { icon: 'folder', iconType: 'ionicons' as const };
};

export const CategoryDetailScreen: React.FC = () => {
  const router = useRouter();
  const { categoryId, categoryName } = useLocalSearchParams<{ categoryId: string; categoryName: string }>();

  const [sections, setSections] = useState<ReportSection[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const id = Number(categoryId);
    if (categoryId && !isNaN(id) && id > 0) {
      loadSections(id);
    } else if (categoryId) {
      setError('Invalid category ID');
      setLoading(false);
    }
  }, [categoryId]);

  const loadSections = async (id: number) => {
    try {
      setLoading(true);
      setError(null);
      const data = await reportApi.getSectionsByCategory(id);
      setSections(data);
    } catch (err: any) {
      console.error('Error loading sections:', err);
      setError(err.message || 'Failed to load sections');
    } finally {
      setLoading(false);
    }
  };

  const handleSectionPress = (section: ReportSection) => {
    router.push({
      pathname: '/my-reports/[categoryId]/[sectionId]',
      params: { 
        categoryId: categoryId || '', 
        sectionId: section.id.toString(),
        sectionName: section.name,
        categoryName: categoryName || ''
      }
    } as any);
  };

  const getThemeForIndex = (index: number) => {
    return SECTION_THEMES[index % SECTION_THEMES.length];
  };

  const renderIcon = (section: ReportSection, theme: typeof SECTION_THEMES[0]) => {
    const iconSize = 24;
    const iconConfig = getSectionIcon(section.name);

    try {
      if (iconConfig.iconType === 'material-community') {
        return <MaterialCommunityIcons name={iconConfig.icon as any} size={iconSize} color={theme.iconColor} />;
      }
      return <Ionicons name={iconConfig.icon as any} size={iconSize} color={theme.iconColor} />;
    } catch {
      return <Ionicons name="folder" size={iconSize} color={theme.iconColor} />;
    }
  };

  return (
    <ScreenWrapper safeArea backgroundColor={COLORS.background}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => router.back()}>
          <Ionicons name="arrow-back" size={24} color={COLORS.primary} />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>{categoryName || 'Category'}</Text>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => router.push('/notifications' as any)}
        >
          <Ionicons name="notifications" size={24} color={COLORS.primary} />
          <View style={styles.notificationBadge}>
            <Text style={styles.badgeText}>3</Text>
          </View>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <ActivityIndicator size="large" color={COLORS.primary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary }}>Loading sections...</Text>
        </View>
      ) : error ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 20 }}>
          <Ionicons name="alert-circle-outline" size={64} color={COLORS.error} />
          <Text style={{ marginTop: 16, color: COLORS.error, textAlign: 'center' }}>{error}</Text>
          <TouchableOpacity 
            style={{ marginTop: 16, paddingHorizontal: 24, paddingVertical: 12, backgroundColor: COLORS.primary, borderRadius: 8 }}
            onPress={() => loadSections(Number(categoryId))}
          >
            <Text style={{ color: '#fff', fontWeight: '600' }}>Retry</Text>
          </TouchableOpacity>
        </View>
      ) : sections.length === 0 ? (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 40 }}>
          <Ionicons name="folder-open-outline" size={64} color={COLORS.textSecondary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary, textAlign: 'center' }}>
            No sections available in this category.
          </Text>
        </View>
      ) : (
        <ScrollView 
          showsVerticalScrollIndicator={false}
          contentContainerStyle={styles.scrollContent}
        >
          {sections.map((section, index) => {
            const theme = getThemeForIndex(index);
            return (
              <TouchableOpacity
                key={section.id}
                style={[styles.subcategoryCard, { backgroundColor: theme.bgColor }]}
                onPress={() => handleSectionPress(section)}
                activeOpacity={0.7}
              >
                <View style={[styles.iconContainerWhite, { backgroundColor: theme.iconBg }]}>
                  {renderIcon(section, theme)}
                </View>
                <View style={styles.subcategoryInfo}>
                  <Text style={styles.subcategoryName}>{section.name}</Text>
                  <Text style={styles.subcategoryCount}>
                    {section.items?.length || 0} {(section.items?.length || 0) === 1 ? 'report' : 'reports'}
                  </Text>
                </View>
                <Ionicons name="chevron-forward" size={24} color={COLORS.textSecondary} />
              </TouchableOpacity>
            );
          })}
        </ScrollView>
      )}
    </ScreenWrapper>
  );
};

