  useEffect(() => {
    loadValue();
  }, [valueId]);

  const loadValue = async () => {
    try {
      setLoading(true);
      const values = await reportApi.getValuesByItem(Number(itemId));
      const foundValue = values.find(v => v.id === Number(valueId));
      setValue(foundValue || null);
    } catch (err: any) {
      console.error('Error loading value:', err);
      showToast('Failed to load record', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = () => {
    router.push({
      pathname: '/my-reports/[categoryId]/[sectionId]/[itemId]/edit/[valueId]',
      params: { 
        categoryId: categoryId || '',
        sectionId: sectionId || '',
        itemId: itemId || '',
        valueId: valueId || '',
        itemName: itemName || '',
        sectionName: sectionName || ''
      }
    } as any);
  };

  const handleDeleteConfirm = async () => {
    setShowDeleteModal(false);
    try {
      setDeleting(true);
      await reportApi.deleteValue(Number(valueId));
      showToast('Record deleted successfully', 'success');
      router.back();
    } catch (err: any) {
      console.error('Error deleting record:', err);
      showToast(err.message || 'Failed to delete record', 'error');
    } finally {
      setDeleting(false);
    }
  };

  const handleDownloadAttachment = async (attachmentId: number) => {
    const url = reportApi.getAttachmentDownloadUrl(attachmentId);
    if (Platform.OS === 'web') {
      window.open(url, '_blank');
    } else {
      await Linking.openURL(url);
    }
  };

  const formatDateSriLanka = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-GB', {
      timeZone: 'Asia/Colombo',
      weekday: 'long',
      day: '2-digit',
      month: 'long',
      year: 'numeric',
    });
  };

  const formatTimeSriLanka = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-GB', {
      timeZone: 'Asia/Colombo',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    });
  };

  if (loading) {
    return (
      <ScreenWrapper safeArea backgroundColor={COLORS.background}>
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <ActivityIndicator size="large" color={COLORS.primary} />
          <Text style={{ marginTop: 16, color: COLORS.textSecondary }}>Loading...</Text>
        </View>
      </ScreenWrapper>
    );
  }

  if (!value) {
    return (
      <ScreenWrapper safeArea backgroundColor={COLORS.background}>
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', padding: 20 }}>
          <Ionicons name="document-text-outline" size={64} color={COLORS.textSecondary} />
          <Text style={{ marginTop: 16, fontSize: 18, fontWeight: '600', color: COLORS.text }}>
            Record not found
          </Text>
          <TouchableOpacity 
            style={{ marginTop: 16, paddingHorizontal: 24, paddingVertical: 12, backgroundColor: COLORS.primary, borderRadius: 8 }}
            onPress={() => router.back()}
          >
            <Text style={{ color: '#fff', fontWeight: '600' }}>Go Back</Text>
          </TouchableOpacity>
        </View>
      </ScreenWrapper>
    );
  }

  return (
    <ScreenWrapper safeArea backgroundColor={COLORS.background}>
      <View style={{
        flexDirection: 'row',
        alignItems: 'center',
        paddingHorizontal: 16,
        paddingVertical: 12,
        backgroundColor: '#fff',
        borderBottomWidth: 1,
        borderBottomColor: '#eee',
      }}>
        <TouchableOpacity 
          style={{ padding: 8, marginRight: 8 }} 
          onPress={() => router.back()}
        >
          <Ionicons name="arrow-back" size={24} color={COLORS.text} />
        </TouchableOpacity>
        <View style={{ flex: 1 }}>
          <Text style={{ fontSize: 18, fontWeight: '700', color: COLORS.text }}>
            Record Details
          </Text>
          <Text style={{ fontSize: 12, color: COLORS.textSecondary }}>
            {itemName}
          </Text>
        </View>
        <TouchableOpacity 
          style={{ padding: 8 }}
          onPress={() => router.push('/notifications' as any)}
        >
          <Ionicons name="notifications" size={24} color={COLORS.primary} />
          <View style={{
            position: 'absolute',
            top: 4,
            right: 4,
            backgroundColor: COLORS.error,
            width: 16,
            height: 16,
            borderRadius: 8,
            justifyContent: 'center',
            alignItems: 'center',
          }}>
            <Text style={{ color: '#fff', fontSize: 10, fontWeight: '600' }}>3</Text>
          </View>
        </TouchableOpacity>
      </View>

      <ScrollView 
        showsVerticalScrollIndicator={false}
        contentContainerStyle={{ padding: 16 }}
      >
        <View style={{
          backgroundColor: '#fff',
          borderRadius: 12,
          padding: 20,
          marginBottom: 16,
        }}>
          <View style={{ marginBottom: 20 }}>
            <Text style={{ fontSize: 12, color: COLORS.textSecondary, marginBottom: 4 }}>
              DATE RECORDED
            </Text>
            <Text style={{ fontSize: 16, fontWeight: '600', color: COLORS.text }}>
              {formatDateSriLanka(value.createdAt)}
            </Text>
            <Text style={{ fontSize: 14, color: COLORS.textSecondary }}>
              at {formatTimeSriLanka(value.createdAt)}
            </Text>
          </View>

          {(value.valueNumber !== null && value.valueNumber !== undefined) && (
            <View style={{ marginBottom: 20 }}>
              <Text style={{ fontSize: 12, color: COLORS.textSecondary, marginBottom: 4 }}>
                VALUE
              </Text>
              <Text style={{ fontSize: 32, fontWeight: '700', color: COLORS.primary }}>
                {value.valueNumber}
              </Text>
            </View>
          )}

          {value.valueText && (
            <View style={{ marginBottom: 20 }}>
              <Text style={{ fontSize: 12, color: COLORS.textSecondary, marginBottom: 4 }}>
                NOTES
              </Text>
              <Text style={{ fontSize: 16, color: COLORS.text, lineHeight: 24 }}>
                {value.valueText}
              </Text>
            </View>
          )}
        </View>

        {value.attachments && value.attachments.length > 0 && (
          <View style={{
            backgroundColor: '#fff',
            borderRadius: 12,
            padding: 16,
            marginBottom: 16,
          }}>
            <Text style={{ fontSize: 16, fontWeight: '600', color: COLORS.text, marginBottom: 12 }}>
              Attachments ({value.attachments.length})
            </Text>
            {value.attachments.map((attachment) => (
              <TouchableOpacity
                key={attachment.id}
                style={{
                  flexDirection: 'row',
                  alignItems: 'center',
                  backgroundColor: '#f5f5f5',
                  borderRadius: 8,
                  padding: 12,
                  marginBottom: 8,
                }}
                onPress={() => handleDownloadAttachment(attachment.id)}
              >
                <Ionicons 
                  name={attachment.fileType?.includes('pdf') ? 'document-text' : 'image'} 
                  size={28} 
                  color={COLORS.primary} 
                />
                <View style={{ flex: 1, marginLeft: 12 }}>
                  <Text style={{ fontSize: 14, fontWeight: '500', color: COLORS.text }} numberOfLines={1}>
                    {attachment.fileUrl.split('/').pop()}
                  </Text>
                  <Text style={{ fontSize: 12, color: COLORS.textSecondary }}>
                    Tap to download
                  </Text>
                </View>
                <Ionicons name="download-outline" size={20} color={COLORS.primary} />
              </TouchableOpacity>
            ))}
          </View>
        )}

        {value.updatedAt !== value.createdAt && (
          <View style={{
            backgroundColor: '#f5f5f5',
            borderRadius: 8,
            padding: 12,
            marginBottom: 16,
          }}>
            <Text style={{ fontSize: 12, color: COLORS.textSecondary }}>
              Last updated: {formatDateSriLanka(value.updatedAt)} at {formatTimeSriLanka(value.updatedAt)}
            </Text>
          </View>
        )}
      </ScrollView>

      <View style={{
        padding: 16,
        backgroundColor: '#fff',
        borderTopWidth: 1,
        borderTopColor: '#eee',
        flexDirection: 'row',
        gap: 12,
      }}>
        <TouchableOpacity
          style={{
            flex: 1,
            backgroundColor: COLORS.primary,
            paddingVertical: 14,
            borderRadius: 12,
            alignItems: 'center',
            flexDirection: 'row',
            justifyContent: 'center',
          }}
          onPress={handleEdit}
        >
          <Ionicons name="pencil" size={20} color="#fff" />
          <Text style={{ color: '#fff', fontWeight: '700', fontSize: 16, marginLeft: 8 }}>
            Edit
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={{
            flex: 1,
            backgroundColor: '#ffebee',
            paddingVertical: 14,
            borderRadius: 12,
            alignItems: 'center',
            flexDirection: 'row',
            justifyContent: 'center',
          }}
          onPress={() => setShowDeleteModal(true)}
          disabled={deleting}
        >
          {deleting ? (
            <ActivityIndicator color={COLORS.error} size="small" />
          ) : (
            <>
              <Ionicons name="trash-outline" size={20} color={COLORS.error} />
              <Text style={{ color: COLORS.error, fontWeight: '700', fontSize: 16, marginLeft: 8 }}>
                Delete
              </Text>
            </>
          )}
        </TouchableOpacity>
      </View>

      <Modal
        visible={showDeleteModal}
        transparent
        animationType="fade"
        onRequestClose={() => setShowDeleteModal(false)}
      >
        <View style={{
          flex: 1,
          backgroundColor: 'rgba(0,0,0,0.5)',
          justifyContent: 'center',
          alignItems: 'center',
          padding: 20,
        }}>
          <View style={{
            backgroundColor: '#fff',
            borderRadius: 16,
            padding: 24,
            width: '100%',
            maxWidth: 340,
          }}>
            <View style={{ alignItems: 'center', marginBottom: 16 }}>
              <View style={{
                width: 56,
                height: 56,
                borderRadius: 28,
                backgroundColor: '#ffebee',
                justifyContent: 'center',
                alignItems: 'center',
                marginBottom: 12,
              }}>
                <Ionicons name="trash-outline" size={28} color={COLORS.error} />
              </View>
              <Text style={{ fontSize: 18, fontWeight: '700', color: COLORS.text, textAlign: 'center' }}>
                Delete Record?
              </Text>
            </View>
            <Text style={{ fontSize: 14, color: COLORS.textSecondary, textAlign: 'center', marginBottom: 24, lineHeight: 20 }}>
              Are you sure you want to delete this record? This action cannot be undone.
            </Text>
            <View style={{ flexDirection: 'row', gap: 12 }}>
              <TouchableOpacity
                style={{
                  flex: 1,
                  paddingVertical: 14,
                  borderRadius: 10,
                  backgroundColor: '#f5f5f5',
                  alignItems: 'center',
                }}
                onPress={() => setShowDeleteModal(false)}
              >
                <Text style={{ fontSize: 16, fontWeight: '600', color: COLORS.text }}>Cancel</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  flex: 1,
                  paddingVertical: 14,
                  borderRadius: 10,
                  backgroundColor: COLORS.error,
                  alignItems: 'center',
                }}
                onPress={handleDeleteConfirm}
              >
                <Text style={{ fontSize: 16, fontWeight: '600', color: '#fff' }}>Delete</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </ScreenWrapper>
  );
};

